    and app_id = #{appId}


    AND id IN

        #{item}

mapper.update:
update file_directory_tree

    child_max_deep = child_max_deep + 1

WHERE 1=1

    and app_id = #{appId}


    AND id IN

        #{item}

mapper.select:
select d.id, d.parent_id, d.is_file, d.name, d.app_id, d.file_id, d.comment,d.hit_count,d.create_time, d.update_time,
f.id as f_id, f.size,f.char_count, f.type, f.address, f.create_time as f_create_time,
f.slice_config, f.status,f.enabled
from file_directory_tree d left outer join file_info_v2 f on d.file_id = f.id  where 1=1 and d.status = 1
and d.id = #{id}
and d.name = #{name}
and d.parent_id = #{parentId}
and d.is_file = #{isFile}
and d.app_id = #{appId}
and d.file_id = #{fileId}
and d.comment = #{comment}
and d.create_time = #{createTime,jdbcType=TIMESTAMP}
and d.update_time = #{updateTime,jdbcType=TIMESTAMP}
and f.status = 5
order by d.is_file asc


         , d.create_time desc 
        , d.create_time asc 


 LIMIT #{start},#{limit}

select count(1)
from file_directory_tree d left outer join file_info_v2 f on d.file_id = f.id  where 1=1
and d.id = #{id}
and d.name = #{name}
and d.parent_id = #{parentId}
and d.is_file = #{isFile}
and d.app_id = #{appId}
and d.file_id = #{fileId}
and d.comment = #{comment}
and d.create_time = #{createTime,jdbcType=TIMESTAMP}
and d.update_time = #{updateTime,jdbcType=TIMESTAMP}

select * from file_directory_tree where is_file = 0
and app_id = #{appId}
and INSTR(name, #{query})

select d.id, d.parent_id, d.is_file, d.name, d.app_id, d.file_id,d.hit_count, d.comment,d.create_time, d.update_time, f.id as f_id, f.size,f.char_count, f.type, f.address, f.create_time as f_create_time, f.slice_config, f.status,f.enabled from file_directory_tree d left outer join file_info_v2 f on d.file_id = f.id where 1=1 and d.status = 1
and d.app_id = #{appId}
and d.is_file = #{isFile}
and INSTR(d.name, #{fileName})
and f.status = 5

SELECT count(1) from file_directory_tree fdt left join repo r on r.id = fdt.app_id
left join file_info_v2 fiv on fiv.id = fdt.file_id
where r.core_repo_id = #{repoId} and fiv.uuid = #{sourceId}
